/* Modernizr 2.5.3 - Placeholder for Cover Oregon Prototype */
console.log('Modernizr 2.5.3 loaded');

// Basic Modernizr functionality
window.Modernizr = {
    // CSS3 features
    cssanimations: true,
    csstransforms: true,
    csstransitions: true,
    flexbox: true,
    
    // HTML5 features
    canvas: !!document.createElement('canvas').getContext,
    video: !!document.createElement('video').canPlayType,
    audio: !!document.createElement('audio').canPlayType,
    localstorage: (function() {
        try {
            localStorage.setItem('test', 'test');
            localStorage.removeItem('test');
            return true;
        } catch(e) {
            return false;
        }
    })(),
    
    // Form features
    input: {
        autocomplete: true,
        autofocus: true,
        list: !!document.createElement('datalist'),
        placeholder: 'placeholder' in document.createElement('input'),
        required: 'required' in document.createElement('input'),
        pattern: 'pattern' in document.createElement('input'),
        max: 'max' in document.createElement('input'),
        min: 'min' in document.createElement('input'),
        step: 'step' in document.createElement('input'),
        multiple: 'multiple' in document.createElement('input')
    },
    
    // Touch support
    touch: 'ontouchstart' in window || navigator.maxTouchPoints,
    
    // Add classes to HTML element
    addTest: function(name, test) {
        this[name] = test;
        var className = test ? name : 'no-' + name;
        document.documentElement.className += ' ' + className;
    }
};

// Add basic feature detection classes to HTML
document.documentElement.className += ' js';
if (window.Modernizr.touch) document.documentElement.className += ' touch';
if (window.Modernizr.cssanimations) document.documentElement.className += ' cssanimations';
if (window.Modernizr.csstransforms) document.documentElement.className += ' csstransforms';
if (window.Modernizr.csstransitions) document.documentElement.className += ' csstransitions';
if (window.Modernizr.flexbox) document.documentElement.className += ' flexbox'; 